#seeing the worldplaces = ['guayaquil','lima','paris','london','hawaii']print("This is the original list:")print(places)print('This is the sorted list of places:')print(sorted(places))print('This is the list without modifications:')print(places)print("Again the sorted list, but in reverse:")print(sorted(places,reverse = True))print("Original again:")print(places)places.reverse()print("Permanent reverse")print(places)places.reverse()print("Changed back to original:")print(places)places.sort()print("Permanently sorted:")print(places)places.sort(reverse = True)print("Permanently sorted in reverse:")print(places)